import React from 'react';
import './styles/style.css';
import Home from './components/Home';
import { BrowserRouter, Routes, Route } from 'react-router-dom';

function App() {
  async function getForecastData() {
    console.log('LOADING...');
    try {
      const locationInput = document.querySelector('#locationInput').value;
      const response = await fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${locationInput}&appid=533055fded414ae06537efb9be138abd`,
        { mode: 'cors' }
      );
      const location = await response.json();

      // GET FORECAST WEATHER DATA FROM THAT LOCATION USING latitude and longitude
      try {
        const response = await fetch(
          `https://api.openweathermap.org/data/2.5/onecall?lat=${location.coord.lat}&lon=${location.coord.lon}&units=metric&exclude={part}&appid=533055fded414ae06537efb9be138abd`,
          { mode: 'cors' }
        );
        const data = await response.json();
        render(data);
      } catch (error) {
        console.log('There was a problem fetching the data:', error);
      }
    } catch (error) {
      console.log('There was a problem fetching your location:', error);
    }
  }

  return (
    <BrowserRouter>
      <div>
        <Routes>
          <Route path='/' element={<Home />} />
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
